参考代码 <pre><code>int alpha_beta(int u, int alph, int beta, bool is_max) {
  if (!son_num[u]) return val[u];
  if (is_max) {
    for (int i = 0; i &lt; son_num[u]; ++i) {
      int d = son[u][i];
      alph = max(alph, alpha_beta(d, alph, beta, !is_max));
      if (alph &gt;= beta) break;
    }
    return alph;
  } else {
    for (int i = 0; i &lt; son_num[u]; ++i) {
      int d = son[u][i];
      beta = min(beta, alpha_beta(d, alph, beta, !is_max));
      if (alph &gt;= beta) break;
    }
    return beta;
  }
}
